{'function': 'async () => {
  // Get the Test Connection button
  const button = document.querySelector('button:has-text("Test Connection")');
  if (!button) return { error: "Button not found" };

  // Click it
  button.click();

  // Wait a moment for the state to update
  await new Promise(resolve => setTimeout(resolve, 100));

  // Check if disabled
  const isDisabled = button.disabled;
  const buttonText = button.textContent;

  return {
    isDisabled,
    buttonText,
    html: button.outerHTML
  };
}'}

{'code': "async (page) => {
  // Get the first Test Connection button
  const button = await page.getByRole('button', { name: 'Test Connection' }).first();

  // Click it
  await button.click();

  // Immediately check if it's disabled
  const isDisabled = await button.isDisabled();
  const buttonText = await button.textContent();

  return {
    isDisabled,
    buttonText: buttonText || 'empty'
  };
}"}

